INSERT INTO `wp_posts` VALUES("65", "1", "2015-01-20 19:19:55", "2015-01-20 19:19:55", "<div class=\"super-hero benefits\">\r\n 	\r\n	<div class=\"super-hero-inner\">\r\n			<div class=\"super-hero-callout reverse\">\r\n				<h2>Meet our Care Coaches.</h2>\r\n                                <p>We spent over two years developing a program specifically designed to assist caregivers and provide caregiver resources through specialized coaching. Our goal? To empower caregivers. Caregiving will never be easy or simple, but we can work with you, share your burden, and help you to enjoy the triumphs.</p>\r\n			</div>\r\n	</div>\r\n\r\n</div>\r\n\r\n\r\n<div class=\"connection-frame\">\r\n   <div class=\"connection-inner\">\r\n      <div class=\"left-half\">\r\n	<img src=\"http://localhost:8888/caregiver-hq/wp-content/themes/Caregiver-HQ/images/circles_temp.jpg\" alt=\"\" title=\"\">\r\n      </div>\r\n\r\n      <div class=\"right-half\">\r\n	<div class=\"title\"><h2>Caregiver Connection</h2></div>\r\n	<div class=\"block\"><h5>We can give you a guiding hand</h5><br><p>“Your advice in your last email was excellent as well. The more I hear it the more I can see more clearly. It feels like I am coming out of a fog after fixating on what is wrong vs. what can help. Thanks so much.”</p></div>\r\n	<div clear=\"all\"></div>\r\n	\r\n        <div class=\"btn\"><a href=\"#\" alt=\"\">More Testimonials</a></div>\r\n      </div>\r\n   </div>\r\n</div>", "Care Coaches", "", "inherit", "open", "open", "", "7-revision-v1", "", "", "2015-01-20 19:19:55", "2015-01-20 19:19:55", "", "7", "http://localhost:8888/caregiver-hq/7-revision-v1/", "0", "revision", "", "0");
